    $(document).ready(function() {
		$('.scroll-section').slick({
			infinite: true,
			slidesToShow: 3,
			slidesToScroll: 1,
			dots: true,
            centerMode: true,
            variableWidth: true
		});
	});
    
    window.addEventListener('scroll', function() {
        var staticMenu = document.getElementById('menu_panel');
        var topMenu = document.getElementById('top_menu_panel');

        if (isElementHidden(staticMenu)) {
            topMenu.classList.remove('hidden');
        } else {
            topMenu.classList.add('hidden');
        }
    });

    function isElementHidden(element) {
        var rect = element.getBoundingClientRect();
        return rect.bottom <= 104;
    }

    function scrollToDiv(name) {
        const element = document.getElementById(name);
        const topOffset = element.offsetTop - 104;
        window.scrollTo({
            top: topOffset,
            behavior: 'smooth'
        });
    }
</script>

